numero = int(input('digite um numero: '))                                       #Variavel recebe um numero que já foi convertido de String para Inteiro.
tab1 = numero * 1                                                               #Variavel recebe o calculo da tabuada de 1
tab2 = numero * 2                                                               #Variavel recebe o calculo da tabuada de 2
tab3 = numero * 3                                                               #Variavel recebe o calculo da tabuada de 3
tab4 = numero * 4                                                               #Variavel recebe o calculo da tabuada de 4
tab5 = numero * 5                                                               #Variavel recebe o calculo da tabuada de 5
tab6 = numero * 6                                                               #Variavel recebe o calculo da tabuada de 6
tab7 = numero * 7                                                               #Variavel recebe o calculo da tabuada de 7
tab8 = numero * 8                                                               #Variavel recebe o calculo da tabuada de 8
tab9 = numero * 9                                                               #Variavel recebe o calculo da tabuada de 9
tab10 = numero * 10                                                             #Variavel recebe o calculo da tabuada de 10
print ('a tabuada de {} é {}, {}, {}, {}, {}, {}, {}, {}, {} e {}'.format
      (numero, tab1, tab2, tab3, tab4, tab5, tab6, tab7, tab8, tab9, tab10))    #Imprime o numero digitado e a tabuada respectiva.
